import React, { useState, useRef } from 'react';
import { Button, message, Select, Modal, InputNumber } from 'antd';
import { CloseOutlined, SoundOutlined } from '@ant-design/icons';
import styled, { keyframes, css } from 'styled-components';
import bgImage from './pics/bg.jpg';
import bgMusic from './music/背景音乐.wav';
import rollMusic from './music/滚动音乐.wav';
import winMusic from './music/中奖音乐.wav';
import { useAudioControl } from './hooks/useAudioControl';

const AppContainer = styled.div`
  min-height: 100vh;
  background: url(${bgImage}) no-repeat center center fixed;
  background-size: cover;
  padding: 20px;
  color: white;
  position: relative;

  & > * {
    position: relative;
    z-index: 1;
  }
`;

const Header = styled.div`
  text-align: center;
  font-size: 24px;
  margin-bottom: 20px;
`;

const MainContent = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  padding: 0 20px;
`;

const DrawBox = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  max-width: 800px;
`;

const PrizeBox = styled.div`
  width: 400px;
  height: 400px;
  background: linear-gradient(135deg, #ff1a1a 0%, #cc0000 100%);
  border-radius: 20px;
  position: relative;
  margin: 40px 0;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const rollAnimation = keyframes`
  0% {
    transform: translateY(-100%);
  }
  100% {
    transform: translateY(100%);
  }
`;

const DrawNumber = styled.div`
  font-size: 120px;
  font-weight: bold;
  text-align: center;
  color: #ffd700;
  text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.5);
  height: 140px;
  line-height: 140px;
  overflow: hidden;
  position: relative;

  ${props => props.isDrawing && css`
    visibility: hidden;

    &::before,
    &::after {
      visibility: visible;
      content: '${props => props.number}';
      position: absolute;
      width: 100%;
      text-align: center;
      animation: ${rollAnimation} 0.2s linear infinite;
    }

    &::before {
      top: -100%;
    }

    &::after {
      top: 100%;
    }
  `}
`;

const ParticipantList = styled.div`
  width: 300px;
  background: rgba(153, 0, 0, 0.3);
  border-radius: 10px;
  padding: 20px;
  backdrop-filter: blur(10px);
`;

const ParticipantItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 10px;
  padding: 10px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 5px;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }
`;

const Avatar = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #fff;
  margin-right: 10px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const BottomControls = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-top: 20px;
  background: rgba(0, 0, 0, 0.2);
  padding: 15px;
  border-radius: 10px;
  width: 100%;
  max-width: 600px;
`;

const PrizeSelect = styled(Select)`
  &.ant-select {
    width: 150px;

    .ant-select-selector {
      background: rgba(0, 0, 0, 0.3) !important;
      border: none !important;
      color: #fff !important;
    }
  }
`;

const NumberControl = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;

  .ant-btn {
    min-width: 40px;
    height: 40px;
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 18px;
