import React, { useState } from 'react';
import { Button, message } from 'antd';
import styled, { keyframes, css } from 'styled-components';

const AppContainer = styled.div`
  min-height: 100vh;
  background: #ff0000;
  padding: 20px;
  color: white;
`;

const Header = styled.div`
  text-align: center;
  font-size: 24px;
  margin-bottom: 20px;
`;

const MainContent = styled.div`
  display: flex;
  gap: 20px;
`;

const DrawBox = styled.div`
  flex: 1;
  background: rgba(0, 0, 0, 0.2);
  border-radius: 10px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const PrizeBox = styled.div`
  width: 300px;
  height: 300px;
  background: #8b0000;
  border: 4px solid #ffd700;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin: 20px 0;
  position: relative;
  overflow: hidden;
`;

const GoldenText = styled.div`
  color: #ffd700;
  font-size: ${props => props.size || '120px'};
  font-weight: bold;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
`;

const rollAnimation = keyframes`
  0% {
    transform: translateY(0);
  }
  100% {
    transform: translateY(-50px);
  }
`;

const DrawNumber = styled.div`
  font-size: 48px;
  font-weight: bold;
  text-align: center;
  color: #ffd700;
  ${props => props.isDrawing && css`
    animation: ${rollAnimation} 0.1s infinite;
  `}
`;

const ParticipantList = styled.div`
  width: 300px;
  background: rgba(0, 0, 0, 0.2);
  border-radius: 10px;
  padding: 20px;
`;

const ParticipantItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 10px;
  padding: 10px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 5px;
`;

const Avatar = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #ffd700;
  margin-right: 10px;
`;

const Controls = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin-top: 20px;
`;

const NumberControl = styled.div`
  display: flex;
  align-items: center;
  background: rgba(0, 0, 0, 0.3);
  border-radius: 5px;
  padding: 5px;
`;

const ControlButton = styled(Button)`
  &.ant-btn {
    background: #ffd700;
    border-color: #ffd700;
    color: #8b0000;
    font-weight: bold;

    &:hover {
      background: #ffe44d;
      border-color: #ffe44d;
    }
  }
`;

const ParticipantCount = styled.div`
  position: absolute;
  bottom: 20px;
  right: 20px;
  color: #ffd700;
`;

function App() {
  const [participants, setParticipants] = useState([]);
  const [currentPrize] = useState({ name: '一等奖', description: '苹果手机' });
  const [isDrawing, setIsDrawing] = useState(false);
  const [currentNumber, setCurrentNumber] = useState(null);
  const [drawCount, setDrawCount] = useState(1);

  const handleNumberChange = (delta) => {
    setDrawCount(prev => Math.max(1, prev + delta));
  };

  const startDraw = () => {
    if (participants.length === 0) {
      message.error('请先生成抽奖号码');
      return;
    }

    setIsDrawing(true);
    const drawInterval = 50;
    let count = 0;
    const maxDraws = 20;

    const animationInterval = setInterval(() => {
      const randomIndex = Math.floor(Math.random() * 100) + 1;
      setCurrentNumber(randomIndex);

      count++;
      if (count >= maxDraws) {
        clearInterval(animationInterval);
        setIsDrawing(false);
      }
    }, drawInterval);
  };

  // 生成1-100的号码
  const generateNumbers = () => {
    const numbers = Array.from({ length: 100 }, (_, i) => i + 1);
    setParticipants(numbers);
    message.success('号码生成成功');
  };

  return (
    <AppContainer>
      <Header>
        <GoldenText size="36px">{currentPrize.name}</GoldenText>
        <div>{currentPrize.description}</div>
      </Header>

      <MainContent>
        <DrawBox>
          <PrizeBox>
            <GoldenText>奖</GoldenText>
            <GoldenText size="36px">抽奖箱</GoldenText>
            <DrawNumber isDrawing={isDrawing}>
              {currentNumber || '?'}
            </DrawNumber>
          </PrizeBox>

          <Controls>
            <NumberControl>
              <Button onClick={() => handleNumberChange(-1)}>-</Button>
              <span style={{ margin: '0 10px', color: '#ffd700' }}>{drawCount}</span>
              <Button onClick={() => handleNumberChange(1)}>+</Button>
            </NumberControl>

            <ControlButton type="primary" size="large" onClick={startDraw} loading={isDrawing}>
              {isDrawing ? '抽奖中...' : '开始抽奖'}
            </ControlButton>

            {participants.length === 0 && (
              <ControlButton onClick={generateNumbers}>
                生成号码
              </ControlButton>
            )}
          </Controls>

          <ParticipantCount>
            参与人数: {participants.length}
          </ParticipantCount>
        </DrawBox>

        <ParticipantList>
          <h3>参与者列表</h3>
          {Array.from({ length: 10 }).map((_, index) => (
            <ParticipantItem key={index}>
              <Avatar />
              <div>参与者 {index + 1}</div>
            </ParticipantItem>
          ))}
        </ParticipantList>
      </MainContent>
    </AppContainer>
  );
}

export default App;
