import React, { useState, useRef, useEffect } from 'react';
import { Button, message, Select, Modal, InputNumber } from 'antd';
import { CloseOutlined, SoundOutlined } from '@ant-design/icons';
import styled, { keyframes, css } from 'styled-components';
import bgImage from './pics/bg.jpg';

const AppContainer = styled.div`
  min-height: 100vh;
  background: url(${bgImage}) no-repeat center center fixed;
  background-size: cover;
  padding: 20px;
  color: white;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(230, 0, 18, 0.85);
    z-index: 0;
  }
`;

const Header = styled.div`
  text-align: center;
  font-size: 24px;
  margin-bottom: 20px;
`;

const MainContent = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  padding: 0 20px;
`;

const DrawBox = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  max-width: 800px;
`;

const PrizeBox = styled.div`
  width: 400px;
  height: 400px;
  background: linear-gradient(135deg, #ff1a1a 0%, #cc0000 100%);
  border-radius: 20px;
  position: relative;
  margin: 40px 0;
  transform: perspective(1000px) rotateX(10deg);
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.3);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0) 100%);
    border-radius: 20px;
  }
`;

const rollAnimation = keyframes`
  0% {
    transform: translateY(0);
  }
  100% {
    transform: translateY(-50px);
  }
`;

const DrawNumber = styled.div`
  font-size: 120px;
