function App() {
  const [participants, setParticipants] = useState([]);
  const [currentPrize, setCurrentPrize] = useState('一等奖');
  const [isDrawing, setIsDrawing] = useState(false);
  const [currentNumber, setCurrentNumber] = useState(null);
  const [winners, setWinners] = useState([]);
  const [isMuted, setIsMuted] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [numberRange, setNumberRange] = useState({ start: 1, end: 100 });
  const animationRef = useRef(null);

  const [audioLoaded, setAudioLoaded] = useState({
    bg: false,
    roll: false,
    win: false
  });

  const bgMusicRef = useRef(new Audio('./music/背景音乐.wav'));
  const rollMusicRef = useRef(new Audio('./music/滚动音乐.wav'));
  const winMusicRef = useRef(new Audio('./music/中奖音乐.wav'));

  useEffect(() => {
    // 音频加载处理
    const loadAudio = (audio, type) => {
      audio.addEventListener('canplaythrough', () => {
        setAudioLoaded(prev => ({ ...prev, [type]: true }));
      });
      audio.addEventListener('error', () => {
        console.error(`Failed to load ${type} audio`);
      });
    };

    loadAudio(bgMusicRef.current, 'bg');
    loadAudio(rollMusicRef.current, 'roll');
    loadAudio(winMusicRef.current, 'win');

    // 设置循环播放
    bgMusicRef.current.loop = true;
    rollMusicRef.current.loop = true;

    // 监听中奖音乐结束事件
    const handleWinMusicEnd = () => {
      if (!isMuted && audioLoaded.bg) {
        try {
          bgMusicRef.current.play();
        } catch (error) {
          console.error('Failed to play background music:', error);
        }
      }
    };

    winMusicRef.current.addEventListener('ended', handleWinMusicEnd);

    // 默认开始播放背景音乐
    if (!isMuted && audioLoaded.bg) {
      try {
        bgMusicRef.current.play();
      } catch (error) {
        console.error('Failed to play initial background music:', error);
      }
    }

    return () => {
      bgMusicRef.current.pause();
      rollMusicRef.current.pause();
      winMusicRef.current.pause();
      winMusicRef.current.removeEventListener('ended', handleWinMusicEnd);
    };
  }, [audioLoaded, isMuted]);

  const toggleMute = () => {
    setIsMuted(!isMuted);

    // 使用 Promise 处理音频切换
    const handleAudioToggle = async () => {
      try {
        if (isMuted) {
          if (isDrawing && audioLoaded.roll) {
            await bgMusicRef.current.pause();
            await rollMusicRef.current.play();
          } else if (audioLoaded.bg) {
            await rollMusicRef.current.pause();
            await bgMusicRef.current.play();
          }
        } else {
          await Promise.all([
            bgMusicRef.current.pause(),
            rollMusicRef.current.pause(),
            winMusicRef.current.pause()
          ]);
        }
      } catch (error) {
        console.error('Error toggling audio:', error);
      }
    };

    handleAudioToggle();
  };

  const deleteWinner = (index) => {
    setWinners(prev => prev.filter((_, i) => i !== index));
  };

  const showNumberRangeModal = () => {
    setIsModalVisible(true);
  };

  const handleModalOk = () => {
    if (numberRange.start >= numberRange.end) {
      message.error('起始号码必须小于结束号码');
      return;
    }
    generateNumbers(numberRange.start, numberRange.end);
    setIsModalVisible(false);
  };

  const generateNumbers = (start, end) => {
    const numbers = Array.from(
      { length: end - start + 1 }, 
      (_, i) => start + i
    );
    setParticipants(numbers);
    message.success('号码生成成功');
  };

  const startDraw = () => {
    if (participants.length === 0) {
      message.error('请先生成抽奖号码');
      return;
    }

    const availableNumbers = participants.filter(
      num => !winners.some(winner => winner.number === num)
    );

    if (availableNumbers.length === 0) {
      message.error('所有号码已抽完');
      return;
    }

    setIsDrawing(true);

    // 切换到滚动音乐
    const switchToRollMusic = async () => {
      if (!isMuted && audioLoaded.roll) {
        try {
          await bgMusicRef.current.pause();
          rollMusicRef.current.currentTime = 0;
          await rollMusicRef.current.play();
        } catch (error) {
          console.error('Failed to switch to roll music:', error);
        }
      }
    };

    switchToRollMusic();

    const draw = () => {
      const randomIndex = Math.floor(Math.random() * availableNumbers.length);
      setCurrentNumber(availableNumbers[randomIndex]);
      animationRef.current = requestAnimationFrame(draw);
    };

    animationRef.current = requestAnimationFrame(draw);
  };

  const stopDraw = () => {
    if (!isDrawing) return;

    cancelAnimationFrame(animationRef.current);
    setIsDrawing(false);

    // 播放中奖音乐
    const playWinMusic = async () => {
      if (!isMuted && audioLoaded.win) {
        try {
          await rollMusicRef.current.pause();
          winMusicRef.current.currentTime = 0;
          await winMusicRef.current.play();
        } catch (error) {
          console.error('Failed to play win music:', error);
        }
      }
    };

    playWinMusic();

    if (currentNumber) {
      setWinners(prev => [...prev, {
        prize: currentPrize,
        number: currentNumber,
        timestamp: Date.now()
      }]);
    }
  };

  return (
    <AppContainer>
      <SoundButton 
        onClick={toggleMute} 
        icon={<SoundOutlined />} 
        $isMuted={isMuted}
      />
      <Header>
        <h2>抽奖系统</h2>
      </Header>

      <MainContent>
        <DrawBox>
          <PrizeBox>
            <DrawNumber isDrawing={isDrawing}>
              {currentNumber || '?'}
            </DrawNumber>
          </PrizeBox>

          <BottomControls>
            <PrizeSelect
              value={currentPrize}
              onChange={value => setCurrentPrize(value)}
            >
              <Select.Option value="一等奖">一等奖</Select.Option>
              <Select.Option value="二等奖">二等奖</Select.Option>
              <Select.Option value="三等奖">三等奖</Select.Option>
            </PrizeSelect>

            <ParticipantCount>
              剩余号码: {participants.length - winners.length}
            </ParticipantCount>

            {!isDrawing ? (
              <StartButton
                type="primary"
                onClick={startDraw}
                disabled={participants.length === winners.length}
              >
                开始抽奖
              </StartButton>
            ) : (
              <StartButton
                type="primary"
                onClick={stopDraw}
              >
                停止
              </StartButton>
            )}

            {participants.length === 0 && (
              <Button onClick={showNumberRangeModal}>
                生成号码
              </Button>
            )}
          </BottomControls>
        </DrawBox>

        <WinnerList>
          <h3>中奖记录</h3>
          {winners.map((winner, index) => (
            <WinnerItem key={index}>
              <WinnerInfo>
                <div className="prize-name">{winner.prize}</div>
                <div className="number">中奖号码: {winner.number}</div>
              </WinnerInfo>
              <CloseOutlined 
                className="delete-button"
                onClick={() => deleteWinner(index)}
              />
            </WinnerItem>
          ))}
        </WinnerList>
      </MainContent>

      <NumberRangeModal
        title="设置号码范围"
        visible={isModalVisible}
        onOk={handleModalOk}
        onCancel={() => setIsModalVisible(false)}
      >
        <div className="range-inputs">
          <InputNumber
            min={1}
            value={numberRange.start}
            onChange={value => setNumberRange(prev => ({ ...prev, start: value }))}
            placeholder="起始号码"
          />
          <span>至</span>
          <InputNumber
            min={1}
            value={numberRange.end}
            onChange={value => setNumberRange(prev => ({ ...prev, end: value }))}
            placeholder="结束号码"
          />
        </div>
      </NumberRangeModal>
    </AppContainer>
  );
}

export default App;
