import React, { useState } from 'react';
import { Layout, Menu, Button, Upload, Input, Select, message } from 'antd';
import { UploadOutlined, TrophyOutlined, HistoryOutlined, SettingOutlined } from '@ant-design/icons';
import styled from 'styled-components';

const { Header, Content, Sider } = Layout;
const { Option } = Select;

const StyledLayout = styled(Layout)`
  min-height: 100vh;
`;

const Logo = styled.div`
  height: 32px;
  margin: 16px;
  color: white;
  font-size: 20px;
  font-weight: bold;
  text-align: center;
`;

const ContentArea = styled(Content)`
  margin: 24px 16px;
  padding: 24px;
  background: #fff;
  min-height: 280px;
`;

function App() {
  const [participants, setParticipants] = useState([]);
  const [prizes, setPrizes] = useState([]);
  const [isDrawing, setIsDrawing] = useState(false);
  const [winners, setWinners] = useState([]);

  const handleFileUpload = (info) => {
    if (info.file.status === 'done') {
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target.result;
        const names = content.split('\n').filter(name => name.trim());
        setParticipants(names);
        message.success(`${info.file.name} uploaded successfully`);
      };
      reader.readAsText(info.file.originFileObj);
    }
  };

  const startDraw = () => {
    if (participants.length === 0) {
      message.error('Please upload participant list first');
      return;
    }
    setIsDrawing(true);
    // Implement drawing animation and logic
    setTimeout(() => {
      const winner = participants[Math.floor(Math.random() * participants.length)];
      setWinners([...winners, winner]);
      setIsDrawing(false);
    }, 2000);
  };

  return (
    <StyledLayout>
      <Header>
        <Logo>Lucky Draw</Logo>
      </Header>
      <Layout>
        <Sider width={200}>
          <Menu
            mode="inline"
            defaultSelectedKeys={['1']}
            style={{ height: '100%', borderRight: 0 }}
          >
            <Menu.Item key="1" icon={<TrophyOutlined />}>
              Draw
            </Menu.Item>
            <Menu.Item key="2" icon={<HistoryOutlined />}>
              History
            </Menu.Item>
            <Menu.Item key="3" icon={<SettingOutlined />}>
              Settings
            </Menu.Item>
          </Menu>
        </Sider>
        <ContentArea>
          <div style={{ marginBottom: 16 }}>
            <Upload
              accept=".txt,.csv"
              customRequest={({ onSuccess }) => setTimeout(() => onSuccess('ok'), 0)}
              onChange={handleFileUpload}
            >
              <Button icon={<UploadOutlined />}>Upload Participant List</Button>
            </Upload>
          </div>
          <div style={{ marginBottom: 16 }}>
            <Button
              type="primary"
              size="large"
              onClick={startDraw}
              loading={isDrawing}
              style={{ width: 200 }}
            >
              {isDrawing ? 'Drawing...' : 'Start Draw'}
            </Button>
          </div>
          <div>
            <h2>Winners</h2>
            {winners.map((winner, index) => (
              <div key={index}>{winner}</div>
            ))}
          </div>
        </ContentArea>
      </Layout>
    </StyledLayout>
  );
}

export default App;
