function App() {
  const [participants, setParticipants] = useState([]);
  const [currentPrize, setCurrentPrize] = useState('一等奖');
  const [isDrawing, setIsDrawing] = useState(false);
  const [currentNumber, setCurrentNumber] = useState(null);
  const [winners, setWinners] = useState([]);
  const [isMuted, setIsMuted] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [numberRange, setNumberRange] = useState({ start: 1, end: 100 });
  const animationRef = useRef(null);

  const [audioState, setAudioState] = useState({
    bgPlaying: false,
    rollPlaying: false,
    winPlaying: false
  });

  const bgMusicRef = useRef(new Audio(bgMusic));
  const rollMusicRef = useRef(new Audio(rollMusic));
  const winMusicRef = useRef(new Audio(winMusic));

  const [hasInteracted, setHasInteracted] = useState(false);

  // 预加载音频
  useEffect(() => {
    const loadAudios = async () => {
      try {
        bgMusicRef.current.preload = 'auto';
        rollMusicRef.current.preload = 'auto';
        winMusicRef.current.preload = 'auto';

        // 设置音量
        bgMusicRef.current.volume = 0.5;
        rollMusicRef.current.volume = 0.5;
        winMusicRef.current.volume = 0.5;

        // 设置循环
        bgMusicRef.current.loop = true;
        rollMusicRef.current.loop = true;
      } catch (error) {
        console.error('Failed to load audio:', error);
      }
    };

    loadAudios();

    return () => {
      try {
        bgMusicRef.current.pause();
        rollMusicRef.current.pause();
        winMusicRef.current.pause();
      } catch (error) {
        console.error('Failed to cleanup audio:', error);
      }
    };
  }, []);

  // 处理背景音乐
  const playBackgroundMusic = async () => {
    try {
      if (!isMuted) {
        // 确保其他音频都暂停
        rollMusicRef.current.pause();
        winMusicRef.current.pause();

        bgMusicRef.current.currentTime = 0;
        await bgMusicRef.current.play();
        setAudioState({ bgPlaying: true, rollPlaying: false, winPlaying: false });
      }
    } catch (error) {
      console.error('Failed to play background music:', error);
    }
  };

  // 处理滚动音乐
  const playRollMusic = async () => {
    try {
      if (!isMuted) {
        // 暂停其他音频
        bgMusicRef.current.pause();
        winMusicRef.current.pause();

        rollMusicRef.current.currentTime = 0;
        await rollMusicRef.current.play();
        setAudioState({ bgPlaying: false, rollPlaying: true, winPlaying: false });
      }
    } catch (error) {
      console.error('Failed to play roll music:', error);
    }
  };

  // 处理中奖音乐
  const playWinMusic = async () => {
    try {
      if (!isMuted) {
        // 暂停其他音频
        bgMusicRef.current.pause();
        rollMusicRef.current.pause();

        winMusicRef.current.currentTime = 0;
        await winMusicRef.current.play();
        setAudioState({ bgPlaying: false, rollPlaying: false, winPlaying: true });
      }
    } catch (error) {
      console.error('Failed to play win music:', error);
    }
  };

  // 监听中奖音乐结束
  useEffect(() => {
    const handleWinMusicEnd = async () => {
      try {
        if (!isMuted) {
          await playBackgroundMusic();
        }
      } catch (error) {
        console.error('Failed to handle win music end:', error);
      }
    };

    winMusicRef.current.addEventListener('ended', handleWinMusicEnd);
    return () => winMusicRef.current.removeEventListener('ended', handleWinMusicEnd);
  }, [isMuted]);

  // 处理静音切换
  const toggleMute = async (e) => {
    e.stopPropagation(); // 阻止事件冒泡
    try {
      const newMutedState = !isMuted;
      setIsMuted(newMutedState);

      if (newMutedState) {
        // 静音：暂停所有音频
        bgMusicRef.current.pause();
        rollMusicRef.current.pause();
        winMusicRef.current.pause();
        setAudioState({ bgPlaying: false, rollPlaying: false, winPlaying: false });
      } else {
        // 取消静音：根据当前状态播放相应音频
        if (isDrawing) {
          await playRollMusic();
        } else {
          await playBackgroundMusic();
        }
      }
    } catch (error) {
      console.error('Failed to toggle mute:', error);
    }
  };

  // 开始抽奖
  const startDraw = async () => {
    if (participants.length === 0) {
      message.error('请先生成抽奖号码');
      return;
    }

    const availableNumbers = participants.filter(
      num => !winners.some(winner => winner.number === num)
    );

    if (availableNumbers.length === 0) {
      message.error('所有号码已抽完');
      return;
    }

    setIsDrawing(true);
    await playRollMusic();

    const draw = () => {
      const randomIndex = Math.floor(Math.random() * availableNumbers.length);
      setCurrentNumber(availableNumbers[randomIndex]);
      animationRef.current = requestAnimationFrame(draw);
    };

    animationRef.current = requestAnimationFrame(draw);
  };

  // 停止抽奖
  const stopDraw = async () => {
    if (!isDrawing) return;

    cancelAnimationFrame(animationRef.current);
    setIsDrawing(false);
    await playWinMusic();

    if (currentNumber) {
      setWinners(prev => [...prev, {
        prize: currentPrize,
        number: currentNumber,
        timestamp: Date.now()
      }]);
    }
  };

  // 初始化音频播放
  useEffect(() => {
    const initAudio = async () => {
      try {
        if (!isMuted && !isDrawing) {
          await playBackgroundMusic();
        }
      } catch (error) {
        console.error('Failed to initialize audio:', error);
      }
    };

    initAudio();
  }, [isMuted, isDrawing]);

  // 处理第一次用户交互
  const handleFirstInteraction = async () => {
    if (!hasInteracted && !isMuted && audioState.bgPlaying) {
      setHasInteracted(true);
      try {
        await bgMusicRef.current.play();
      } catch (error) {
        console.error('Failed to play background music:', error);
      }
    }
  };

  const deleteWinner = (index) => {
    setWinners(prev => prev.filter((_, i) => i !== index));
  };

  const showNumberRangeModal = () => {
    setIsModalVisible(true);
  };

  const handleModalOk = () => {
    if (numberRange.start >= numberRange.end) {
      message.error('起始号码必须小于结束号码');
      return;
    }
    generateNumbers(numberRange.start, numberRange.end);
    setIsModalVisible(false);
  };

  const generateNumbers = (start, end) => {
    const numbers = Array.from(
      { length: end - start + 1 }, 
      (_, i) => start + i
    );
    setParticipants(numbers);
    message.success('号码生成成功');
  };

  return (
    <AppContainer onClick={handleFirstInteraction}>
      <SoundButton 
        onClick={toggleMute} 
        icon={<SoundOutlined style={{ color: isMuted ? '#999' : '#fff' }} />}
        style={{ background: isMuted ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 255, 255, 0.2)' }}
      />
      <Header>
        <h2>抽奖系统</h2>
      </Header>

      <MainContent>
        <DrawBox>
          <PrizeBox>
            <DrawNumber isDrawing={isDrawing}>
              {currentNumber || '?'}
            </DrawNumber>
          </PrizeBox>

          <BottomControls>
            <PrizeSelect
              value={currentPrize}
              onChange={value => setCurrentPrize(value)}
            >
              <Select.Option value="一等奖">一等奖</Select.Option>
              <Select.Option value="二等奖">二等奖</Select.Option>
              <Select.Option value="三等奖">三等奖</Select.Option>
            </PrizeSelect>

            <ParticipantCount>
              剩余号码: {participants.length - winners.length}
            </ParticipantCount>

            {!isDrawing ? (
              <StartButton
                type="primary"
                onClick={startDraw}
                disabled={participants.length === winners.length}
              >
                开始抽奖
              </StartButton>
            ) : (
              <StartButton
                type="primary"
                onClick={stopDraw}
              >
                停止
              </StartButton>
            )}

            {participants.length === 0 && (
              <Button onClick={showNumberRangeModal}>
                生成号码
              </Button>
            )}
          </BottomControls>
        </DrawBox>

        <WinnerList>
          <h3>中奖记录</h3>
          {winners.map((winner, index) => (
            <WinnerItem key={index}>
              <WinnerInfo>
                <div className="prize-name">{winner.prize}</div>
                <div className="number">中奖号码: {winner.number}</div>
              </WinnerInfo>
              <CloseOutlined 
                className="delete-button"
                onClick={() => deleteWinner(index)}
              />
            </WinnerItem>
          ))}
        </WinnerList>
      </MainContent>

      <NumberRangeModal
        title="设置号码范围"
        visible={isModalVisible}
        onOk={handleModalOk}
        onCancel={() => setIsModalVisible(false)}
      >
        <div className="range-inputs">
          <InputNumber
            min={1}
            value={numberRange.start}
            onChange={value => setNumberRange(prev => ({ ...prev, start: value }))}
            placeholder="起始号码"
          />
          <span>至</span>
          <InputNumber
            min={1}
            value={numberRange.end}
            onChange={value => setNumberRange(prev => ({ ...prev, end: value }))}
            placeholder="结束号码"
          />
        </div>
      </NumberRangeModal>
    </AppContainer>
  );
}

export default App;
