import React, { useState, useRef } from 'react';
import { Button, message, Select } from 'antd';
import styled, { keyframes, css } from 'styled-components';

const AppContainer = styled.div`
  min-height: 100vh;
  background: #e60012;
  padding: 20px;
  color: white;
  position: relative;
`;

const Header = styled.div`
  text-align: center;
  font-size: 24px;
  margin-bottom: 20px;
`;

const MainContent = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  padding: 0 20px;
`;

const DrawBox = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  max-width: 800px;
`;

const PrizeBox = styled.div`
  width: 400px;
  height: 400px;
  background: linear-gradient(135deg, #ff1a1a 0%, #cc0000 100%);
  border-radius: 20px;
  position: relative;
  margin: 40px 0;
  transform: perspective(1000px) rotateX(10deg);
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.3);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0) 100%);
    border-radius: 20px;
  }
`;

const rollAnimation = keyframes`
  0% {
    transform: translateY(0);
  }
  100% {
    transform: translateY(-50px);
  }
`;

const DrawNumber = styled.div`
  font-size: 72px;
  font-weight: bold;
  text-align: center;
  color: #ffd700;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  ${props => props.isDrawing && css`
    animation: ${rollAnimation} 0.1s infinite;
  `}
`;

const ParticipantList = styled.div`
  width: 300px;
  background: rgba(153, 0, 0, 0.3);
  border-radius: 10px;
  padding: 20px;
  backdrop-filter: blur(10px);
`;

const ParticipantItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 10px;
  padding: 10px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 5px;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }
`;

const Avatar = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #fff;
  margin-right: 10px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const BottomControls = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-top: 20px;
  background: rgba(0, 0, 0, 0.2);
  padding: 15px;
  border-radius: 10px;
  width: 100%;
  max-width: 600px;
`;

const PrizeSelect = styled(Select)`
  &.ant-select {
    width: 150px;

    .ant-select-selector {
      background: rgba(0, 0, 0, 0.3) !important;
      border: none !important;
      color: #fff !important;
    }
  }
`;

const NumberControl = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;

  .ant-btn {
    min-width: 40px;
    height: 40px;
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 18px;
    background: rgba(0, 0, 0, 0.3);
    border: none;
    color: #fff;

    &:hover {
      background: rgba(0, 0, 0, 0.5);
    }
  }
`;

const StartButton = styled(Button)`
  &.ant-btn {
    height: 40px;
    padding: 0 30px;
    background: linear-gradient(135deg, #ffd700 0%, #ffaa00 100%);
    border: none;
    font-size: 18px;
    font-weight: bold;
    color: #8b0000;

    &:hover {
      background: linear-gradient(135deg, #ffe44d 0%, #ffbb33 100%);
    }
  }
`;

const ParticipantCount = styled.div`
  color: #ffd700;
  font-size: 16px;
  display: flex;
  align-items: center;
  gap: 5px;

  &::before {
    content: '参与人数:';
    color: #fff;
  }
`;

const Logo = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  width: 260px;
  height: 70px;
  background: #fff;
  border-radius: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  color: #e60012;
  font-weight: bold;
`;

const WinnerList = styled.div`
  width: 300px;
  background: rgba(153, 0, 0, 0.3);
  border-radius: 10px;
  padding: 20px;
  backdrop-filter: blur(10px);
`;

const WinnerItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 10px;
  padding: 10px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 5px;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }
`;

const WinnerInfo = styled.div`
  display: flex;
  flex-direction: column;

  .prize-name {
    color: #ffd700;
    font-weight: bold;
    margin-bottom: 4px;
  }

  .number {
    color: #fff;
  }
`;

function App() {
  const [participants, setParticipants] = useState([]);
  const [currentPrize, setCurrentPrize] = useState('一等奖');
  const [isDrawing, setIsDrawing] = useState(false);
  const [currentNumber, setCurrentNumber] = useState(null);
  const [winners, setWinners] = useState([]);
  const animationRef = useRef(null);

  const startDraw = () => {
    if (participants.length === 0) {
      message.error('请先生成抽奖号码');
      return;
    }

    // 过滤掉已中奖的号码
    const availableNumbers = participants.filter(
      num => !winners.some(winner => winner.number === num)
    );

    if (availableNumbers.length === 0) {
      message.error('所有号码已抽完');
      return;
    }

    setIsDrawing(true);
    const drawInterval = 50;

    const draw = () => {
      const randomIndex = Math.floor(Math.random() * availableNumbers.length);
      setCurrentNumber(availableNumbers[randomIndex]);
      animationRef.current = requestAnimationFrame(draw);
    };

    animationRef.current = requestAnimationFrame(draw);
  };

  const stopDraw = () => {
    if (!isDrawing) return;

    cancelAnimationFrame(animationRef.current);
    setIsDrawing(false);

    // 添加到中奖列表
    if (currentNumber) {
      setWinners(prev => [...prev, {
        prize: currentPrize,
        number: currentNumber,
        timestamp: Date.now()
      }]);
    }
  };

  const generateNumbers = () => {
    const numbers = Array.from({ length: 100 }, (_, i) => i + 1);
    setParticipants(numbers);
    message.success('号码生成成功');
  };

  return (
    <AppContainer>
      <Logo>LOGO 260 x 70</Logo>
      <Header>
        <h2>抽奖系统</h2>
      </Header>

      <MainContent>
        <DrawBox>
          <PrizeBox>
            <DrawNumber isDrawing={isDrawing}>
              {currentNumber || '?'}
            </DrawNumber>
          </PrizeBox>

          <BottomControls>
            <PrizeSelect
              value={currentPrize}
              onChange={value => setCurrentPrize(value)}
            >
              <Select.Option value="一等奖">一等奖</Select.Option>
              <Select.Option value="二等奖">二等奖</Select.Option>
              <Select.Option value="三等奖">三等奖</Select.Option>
            </PrizeSelect>

            <ParticipantCount>
              剩余号码: {participants.length - winners.length}
            </ParticipantCount>

            {!isDrawing ? (
              <StartButton
                type="primary"
                onClick={startDraw}
                disabled={participants.length === winners.length}
              >
                开始抽奖
              </StartButton>
            ) : (
              <StartButton
                type="primary"
                onClick={stopDraw}
              >
                停止
              </StartButton>
            )}

            {participants.length === 0 && (
              <Button onClick={generateNumbers}>
                生成号码
              </Button>
            )}
          </BottomControls>
        </DrawBox>

        <WinnerList>
          <h3>中奖记录</h3>
          {winners.map((winner, index) => (
            <WinnerItem key={index}>
              <WinnerInfo>
                <div className="prize-name">{winner.prize}</div>
                <div className="number">中奖号码: {winner.number}</div>
              </WinnerInfo>
            </WinnerItem>
          ))}
        </WinnerList>
      </MainContent>
    </AppContainer>
  );
}

export default App;
