import { useState, useEffect, useRef } from 'react';

export const useAudioControl = (bgMusic, rollMusic, winMusic) => {
  const [isMuted, setIsMuted] = useState(false);
  const [volume, setVolume] = useState(0.3); // 存储原始音量
  const bgAudioRef = useRef(null);
  const rollAudioRef = useRef(null);
  const winAudioRef = useRef(null);

  useEffect(() => {
    // 初始化音频
    bgAudioRef.current = new Audio(bgMusic);
    rollAudioRef.current = new Audio(rollMusic);
    winAudioRef.current = new Audio(winMusic);

    // 设置音频属性
    if (bgAudioRef.current) {
      bgAudioRef.current.volume = volume;
      bgAudioRef.current.loop = true;
      // 自动开始播放背景音乐
      bgAudioRef.current.play().catch(console.error);
    }
    if (rollAudioRef.current) {
      rollAudioRef.current.volume = 0.7;
    }
    if (winAudioRef.current) {
      winAudioRef.current.volume = 0.8;
    }

    // 监听音效结束事件，恢复背景音乐
    const handleEffectEnd = () => {
      if (!isMuted && bgAudioRef.current) {
        bgAudioRef.current.play().catch(console.error);
      }
    };

    rollAudioRef.current.addEventListener('ended', handleEffectEnd);
    winAudioRef.current.addEventListener('ended', handleEffectEnd);

    return () => {
      if (bgAudioRef.current) {
        bgAudioRef.current.pause();
      }
      if (rollAudioRef.current) {
        rollAudioRef.current.pause();
        rollAudioRef.current.removeEventListener('ended', handleEffectEnd);
      }
      if (winAudioRef.current) {
        winAudioRef.current.pause();
        winAudioRef.current.removeEventListener('ended', handleEffectEnd);
      }
    };
  }, [bgMusic, rollMusic, winMusic, isMuted]);

  const toggleMute = () => {
    setIsMuted(!isMuted);
    if (bgAudioRef.current) {
      if (isMuted) {
        // 取消静音，恢复原来的音量
        bgAudioRef.current.volume = volume;
        bgAudioRef.current.play().catch(console.error);
      } else {
        // 静音
        bgAudioRef.current.volume = 0;
        bgAudioRef.current.pause();
        rollAudioRef.current?.pause();
        winAudioRef.current?.pause();
      }
    }
  };

  const playRollMusic = () => {
    if (!rollAudioRef.current || isMuted) return;

    // 暂停其他音频
    bgAudioRef.current?.pause();
    winAudioRef.current?.pause();

    // 播放滚动音效
    rollAudioRef.current.currentTime = 0;
    rollAudioRef.current.play().catch(error => {
      console.error('播放滚动音效失败:', error);
      // 如果播放失败，尝试恢复背景音乐
      if (!isMuted && bgAudioRef.current) {
        bgAudioRef.current.play().catch(console.error);
      }
    });
  };

  const playWinMusic = () => {
    if (!winAudioRef.current || isMuted) return;

    // 暂停其他音频
    bgAudioRef.current?.pause();
    rollAudioRef.current?.pause();

    // 播放中奖音效
    winAudioRef.current.currentTime = 0;
    winAudioRef.current.play().catch(error => {
      console.error('播放中奖音效失败:', error);
      // 如果播放失败，尝试恢复背景音乐
      if (!isMuted && bgAudioRef.current) {
        bgAudioRef.current.play().catch(console.error);
      }
    });
  };

  return {
    isMuted,
    toggleMute,
    playRollMusic,
    playWinMusic
  };
};
