import React, { useState } from 'react';
import { Button, message, Select } from 'antd';
import styled, { keyframes, css } from 'styled-components';

const AppContainer = styled.div`
  min-height: 100vh;
  background: #e60012;
  padding: 20px;
  color: white;
  position: relative;
`;

const Header = styled.div`
  text-align: right;
  font-size: 24px;
  margin-bottom: 20px;
  padding-right: 20px;
`;

const MainContent = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  padding: 0 20px;
`;

const DrawBox = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  max-width: 800px;
`;

const PrizeBox = styled.div`
  width: 400px;
  height: 400px;
  background: linear-gradient(135deg, #ff1a1a 0%, #cc0000 100%);
  border-radius: 20px;
  position: relative;
  margin: 40px 0;
  transform: perspective(1000px) rotateX(10deg);
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.3);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0) 100%);
    border-radius: 20px;
  }
`;

const DrawNumber = styled.div`
  font-size: 72px;
  font-weight: bold;
  text-align: center;
  color: #ffd700;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  ${props => props.isDrawing && css`
    animation: ${rollAnimation} 0.1s infinite;
  `}
`;

const ParticipantList = styled.div`
  width: 300px;
  background: rgba(153, 0, 0, 0.3);
  border-radius: 10px;
  padding: 20px;
  backdrop-filter: blur(10px);
`;

const ParticipantItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 10px;
  padding: 10px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 5px;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }
`;

const Avatar = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #fff;
  margin-right: 10px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const BottomControls = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-top: 20px;
  background: rgba(0, 0, 0, 0.2);
  padding: 15px;
  border-radius: 10px;
  width: 100%;
  max-width: 600px;
`;

const PrizeSelect = styled(Select)`
  &.ant-select {
    width: 150px;

    .ant-select-selector {
      background: rgba(0, 0, 0, 0.3) !important;
      border: none !important;
      color: #fff !important;
    }
  }
`;

const NumberControl = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;

  .ant-btn {
    min-width: 40px;
    height: 40px;
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 18px;
    background: rgba(0, 0, 0, 0.3);
    border: none;
    color: #fff;

    &:hover {
      background: rgba(0, 0, 0, 0.5);
    }
  }
`;

const StartButton = styled(Button)`
  &.ant-btn {
    height: 40px;
    padding: 0 30px;
    background: linear-gradient(135deg, #ffd700 0%, #ffaa00 100%);
    border: none;
    font-size: 18px;
    font-weight: bold;
    color: #8b0000;

    &:hover {
      background: linear-gradient(135deg, #ffe44d 0%, #ffbb33 100%);
    }
  }
`;

const ParticipantCount = styled.div`
  color: #ffd700;
  font-size: 16px;
  display: flex;
  align-items: center;
  gap: 5px;

  &::before {
    content: '参与人数:';
    color: #fff;
  }
`;

const Logo = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  width: 260px;
  height: 70px;
  background: #fff;
  border-radius: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  color: #e60012;
  font-weight: bold;
`;

function App() {
  const [participants, setParticipants] = useState([]);
  const [currentPrize, setCurrentPrize] = useState({ name: '一等奖', description: '苹果手机' });
  const [isDrawing, setIsDrawing] = useState(false);
  const [currentNumber, setCurrentNumber] = useState(null);
  const [drawCount, setDrawCount] = useState(1);

  const handleNumberChange = (delta) => {
    setDrawCount(prev => Math.max(1, prev + delta));
  };

  const startDraw = () => {
    if (participants.length === 0) {
      message.error('请先生成抽奖号码');
      return;
    }

    setIsDrawing(true);
    const drawInterval = 50;
    let count = 0;
    const maxDraws = 20;

    const animationInterval = setInterval(() => {
      const randomIndex = Math.floor(Math.random() * 100) + 1;
      setCurrentNumber(randomIndex);

      count++;
      if (count >= maxDraws) {
        clearInterval(animationInterval);
        setIsDrawing(false);
      }
    }, drawInterval);
  };

  const generateNumbers = () => {
    const numbers = Array.from({ length: 100 }, (_, i) => i + 1);
    setParticipants(numbers);
    message.success('号码生成成功');
  };

  return (
    <AppContainer>
      <Logo>LOGO 260 x 70</Logo>
      <Header>
        <div style={{ color: '#ffd700', fontSize: '24px' }}>{currentPrize.name}</div>
        <div>{currentPrize.description}</div>
      </Header>

      <MainContent>
        <DrawBox>
          <PrizeBox>
            <DrawNumber isDrawing={isDrawing}>
              {currentNumber || '?'}
            </DrawNumber>
          </PrizeBox>

          <BottomControls>
            <PrizeSelect
              defaultValue="一等奖"
              onChange={value => setCurrentPrize({ name: value, description: '苹果手机' })}
            >
              <Select.Option value="一等奖">一等奖</Select.Option>
              <Select.Option value="二等奖">二等奖</Select.Option>
              <Select.Option value="三等奖">三等奖</Select.Option>
            </PrizeSelect>

            <NumberControl>
              <Button onClick={() => handleNumberChange(-1)}>-</Button>
              <span>{drawCount}</span>
              <Button onClick={() => handleNumberChange(1)}>+</Button>
            </NumberControl>

            <ParticipantCount>{participants.length}</ParticipantCount>

            <StartButton
              type="primary"
              onClick={startDraw}
              loading={isDrawing}
            >
              {isDrawing ? '抽奖中...' : '开始抽奖'}
            </StartButton>

            {participants.length === 0 && (
              <Button onClick={generateNumbers}>
                生成号码
              </Button>
            )}
          </BottomControls>
        </DrawBox>

        <ParticipantList>
          <h3>参与者列表</h3>
          {Array.from({ length: 10 }).map((_, index) => (
            <ParticipantItem key={index}>
              <Avatar />
              <div>参与者 {index + 1}</div>
            </ParticipantItem>
          ))}
        </ParticipantList>
      </MainContent>
    </AppContainer>
  );
}

export default App;
